(window.webpackJsonp=window.webpackJsonp||[]).push([[26],{502:function(t,n,o){"use strict";o.r(n);var r=o(4),s=Object(r.a)({},(function(){var t=this,n=t.$createElement,o=t._self._c||n;return o("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[o("p",[o("img",{attrs:{src:"http://static.wuhaoquan.com/20200610.jpg",alt:""}})]),t._v(" "),o("p",[t._v("最近几天，利用晚上的时间再次把博客搭了起来；是的，准确的说是再次，之前就搭过好几次了，每次都信誓旦旦地扬言要执行所谓的“写作计划”，最后没多久都不了了之了，想到这个事儿，就会觉得很可惜，也很惭愧。")]),t._v(" "),o("p",[t._v("在我们的传统中，自古就有“尚文”的文化意识，通常情况下，我们都会认为凡是成文的东西，一定是来源于现实又必须是要高于现实的！有这样的心里意识在，执行写作计划就会比较困难，因为写作者首先对自己的输出有了一个基本的要求，而对于刚刚开始练习写作的同学来讲，无疑我们很难做到可以持续地、高质量地且都达到了内心及格线地输出内容。有尝试写作的经历的人，一定都有这样的感触，达到持续输出，这个确实有一定的难度。可能这是所有写作计划无疾而终的根本原因吧，其实这种心理意识，本身是一种羁绊，得首先把它跨越过去才行。")]),t._v(" "),o("p",[t._v("这一次，重建博客，重拾写作计划，算是有多方面的原因，一来是想对自己再次发起一个挑战，再有就是是这半年来，初为人父，有些许来自父亲视角的体悟，想把它们记录下来。譬如说，有一次在深夜里爬起来给孩子冲奶，看着她，一点一点地把她喂饱，再看着她在婴儿床里慢慢蠕动。其实那一刻，我突然特别特别想念在远方的父亲，特别想念他，我想当年，他也一定也是这样，百般地呵护着我，保护着我；总之，等等类似这样细碎的零散的各种念想吧，散步在生活的各个角落里，我觉得这些都是生命中非常难能可贵的东西。")]),t._v(" "),o("p",[t._v("今天，作为一个自认为是个内心细腻、胸中充满沟壑的男人，再次开启了他的写作计划。希望在往后的日子里，他能坚持写写，能有所收获，无论留下的文字是温暖的还是平淡的，都应该祝福他。")]),t._v(" "),o("p",[t._v("晚安。")]),t._v(" "),o("div",{staticClass:"custom-block tip"},[o("p",[o("strong",[t._v("Author")]),t._v(": wuhaoquan "),o("br")]),t._v(" "),o("p",[o("strong",[t._v("First published on")]),t._v(" "),o("a",{attrs:{href:"https://wuhaoquan.com",target:"_blank",rel:"noopener noreferrer"}},[t._v("https://wuhaoquan.com"),o("OutboundLink")],1)])])])}),[],!1,null,null,null);n.default=s.exports}}]);